import './App.css';
import React, {useState} from "react";
import axios from "axios";
import {useNavigate} from "react-router-dom";

function App(props) {
  const history = useNavigate();  
  const [user, setUser] = useState("");
  const [erro, setErro] = useState(false);
  
  function handlePesquisa() {
    axios.get(`https://api.github.com/users/${user}/repos`).then(response => {
        const repositories = response.data;
        const repositoriesName = [];

        repositories.map((repository) => {
            repositoriesName.push(repository.name);
        });
        localStorage.setItem("repositoriesName", JSON.stringify(repositoriesName));
        history("/repositories");
    })
    .catch(err => {
        setErro(true);
    });
  }
  
  return (
    <div>
        <div>
            <h1>Repositories from GitHub</h1>
            <p>{user}</p>
            <input className="userInput" placeholder="Insert the username" value={user} onChange={e => setUser(e.target.value)}/>
            <button type="button" onClick={handlePesquisa}>Search</button>
        </div>
      {erro ? <span style={{color: "red"}}>Error. Please try again.</span> : ""}
    </div>
  );
}

export default App;
